One.onLoad(
    class {
        static sweetAlert2() {
            let e = Swal.mixin({
                buttonsStyling: !1,
                target: "#page-container",
                customClass: {
                    confirmButton: "btn btn-success m-1",
                    cancelButton: "btn btn-danger m-1",
                    input: "form-control",
                },
            });

            document
                .querySelector(".js-swal-confirm")
                .addEventListener("click", (t) => {
                    e.fire({
                        title: "Are you sure?",
                        text: "You will not be able to recover this imaginary file!",
                        icon: "warning",
                        showCancelButton: !0,
                        customClass: {
                            confirmButton: "btn btn-submit btn-success m-1",
                            cancelButton: "btn btn-secondary m-1",
                        },
                        confirmButtonText: "Yes, delete it!",
                        html: !1,
                        preConfirm: (e) =>
                            new Promise((e) => {
                                setTimeout(() => {
                                    e();
                                }, 50);
                            }),
                    }).then((t) => {
                        t.value
                            ? e.fire(
                                  "Deleted!",
                                  "Your imaginary file has been deleted.",
                                  "success"
                              )
                            : "cancel" === t.dismiss &&
                              e.fire(
                                  "Cancelled",
                                  "Your imaginary file is safe :)",
                                  "error"
                              );
                    });
                });
        }
        static init() {
            this.sweetAlert2();
        }
    }.init()
);
